svg.script:
 var dark = "#414141";
 var blue = "#2328ff";
 var blueFaded = "#f7f7ff";
 var pink = "#9f81b3";
 var pinkFaded = "#fbf0f8";

 var r1 = 45;
 var r2 = 85;

 function change(evt) {
   var target = evt.target;
   var radius = target.getAttribute("r");

   if (radius == r2) {
     radius = r1;
   } else {
     radius = r2;
   }

   target.setAttribute("r",radius);
}
